<style lang="scss">
.footer {
  display: none;
  &__logo {
    width: 10em;
    height: 1.8em;
    background: var(--logo) center/contain no-repeat;
    align-self: center;
  }
  @include md {
    @include flexArrange(space-between);
    padding: 6.4rem 1.5rem 8rem;
    font-size: 1.4rem;
    background-color: var(--footer-bg);
    &__section {
      &-title {
        color: var(--basic-color);
      }
      &-lists {
        padding: 0;
        margin: 1.5em 0 0;
      }
      &-list--text {
        margin-bottom: 0.5em;
        text-decoration: none;
        color: var(--footer-font-color);
        &:hover {
          color: $main-button-color;
        }
      }
      &:last-child .footer__section-title {
        margin-bottom: 1em;
      }
      &-socialMedia {
        width: 10rem;
        height: 2.4rem;
        @include flexArrange(space-between);
        &-FB,
        &-IG,
        &-WA {
          width: 2.4rem;
          height: 2.4rem;
          @include flexArrange(false, center);
          cursor: pointer;
        }
      }
    }
  }
  @include lg {
    font-size: 1.6rem;
    padding-inline: 12%;
  }
}

.logo .word {
  fill: var(--basic-color);
}
</style>
